import { BrowserRouter, Route, Switch } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import moment from 'moment'

import Admin from "./Admin"

function App() {

  const [trackers, setTrackers] = useState([])
  const [newUrl, setNewUrl] = useState("")
  const [newRedirect1, setNewRedirect1] = useState("")
  const [newRedirect2, setNewRedirect2] = useState("")

  useEffect(()=>{
    setTrackers([
      {
        url: "1.com",
        redirec1: "1a.com",
        redirec2: "1b.com"
      },
      {
        url: "2.com",
        redirec1: "2a.com",
        redirec2: "2b.com"
      },
      {
        url: "3.com",
        redirec1: "3a.com",
        redirec2: "3b.com"
      }
    ])
  }, [])

  const handleNewTracker = () => {
    setTrackers(trackers => [...trackers, {url: newUrl}])
    setNewUrl("")
    setNewRedirect1("")
    setNewRedirect2("")
  }

  return (
    <Switch>
        <Route path="/" component={Admin} exact />
        <Route path="/1" component={One} />
        <Route path="/1a" component={OneA} />
        <Route path="/1b" component={OneB} />
        <Route path="/2" component={Two} />
        <Route path="/2a" component={TwoA} />
        <Route path="/2b" component={TwoB} />
    </Switch>
  );
}

export default App;
