import React, { useEffect, useState} from 'react';
import './App.css';
import axios from 'axios'
import moment from 'moment'


function Admin(props) {

  const [trackers, setTrackers] = useState([])
  const [newUrl, setNewUrl] = useState("")
  const [newRedirect1, setNewRedirect1] = useState("")
  const [newRedirect2, setNewRedirect2] = useState("")
  const [reloadData, setReloadData] = useState(false)

  useEffect(()=>{
    axios.get("/getTrackers").then(data=>{
      setTrackers(data.data)
    })
  }, [])

  useEffect(()=>{
    if(reloadData){
      axios.get("/getTrackers").then(data=>{
        setTrackers(data.data)
        setReloadData(false)
      })
    }
    if(props.reload){
      axios.get("/getTrackers").then(data=>{
        setTrackers(data.data)
        props.setReload(false)
      })
    }


  }, [reloadData, props.reload])

  const handleNewTracker = () => {
    axios.post("/addTracker", {url: newUrl, redirect1:newRedirect1, redirect2:newRedirect2}).then(data=>{
      if(data.data === "tracker added")
      setReloadData(true)
      setNewUrl("")
      setNewRedirect1("")
      setNewRedirect2("")
    })
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={'https://www.crigenetics.com/hubfs/html/ancestry/images/dna-logo.png'} className="App-logo" alt="logo" />
      </header>
      <div className="tracker-container">
        {
          trackers.map(tracker=>{
            return (
              <div key={tracker._id} className="tracker">
                <div className="routes">
                  URL: {tracker.url} - {tracker.trackerOn ? "ON" : "OFF"}
                </div>
                <div className="routes">
                  Redirect 1: {tracker.redirect1}
                </div>
                <div className="stats">
                  <div>
                    Views: {tracker.redirect1Views} 
                  </div>
                  <div>
                    Unique: 
                  </div>
                  <div>
                    Total:
                  </div>
                  <div>
                    Conversions:
                  </div>
                </div>
                <div className="routes">
                  Redirect 2: {tracker.redirect2}
                </div>
                <div className="stats">
                  <div>
                    Views: {tracker.redirect2Views} 
                  </div>
                  <div>
                    Unique: 
                  </div>
                  <div>
                    Total:
                  </div>
                  <div>
                    Conversions:
                  </div>
                </div>
              </div>
            )
          })
        }
        <div className="add-tracker">
          <div className="routes">
            URL: <input value={newUrl} onChange={(event)=>{setNewUrl(event.target.value)}}/>
          </div>
          <div className="routes">
            Redirect 1: <input value={newRedirect1} onChange={(event)=>{setNewRedirect1(event.target.value)}}/>
          </div>
          <div className="routes">
            Redirect 2: <input value={newRedirect2} onChange={(event)=>{setNewRedirect2(event.target.value)}}/>
          </div>
          <button onClick={handleNewTracker} style={{alignSelf: "center"}}>Add New Tracker</button>
        </div>
      </div>
    </div>
  );
}

export default Admin;
