import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import moment from 'moment'

function App() {

  const [trackers, setTrackers] = useState([])
  const [newUrl, setNewUrl] = useState("")

  useEffect(()=>{
    setTrackers([
      {
        url: "1.com"
      },
      {
        url: "2.com"
      },
      {
        url: "3.com"
      }
    ])
  }, [])

  const handleNewTracker = () => {
    setTrackers(trackers => [...trackers, {url: newUrl}])
    setNewUrl("")
  }

  console.log(newUrl)

  return (
    <div className="App">
      <header className="App-header">
        <img src={'https://www.crigenetics.com/hubfs/html/ancestry/images/dna-logo.png'} className="App-logo" alt="logo" />
      </header>
      <div className="tracker-container">
        {
          trackers.map(tracker=>{
            return (
              <div className="tracker">
                <div className="routes">
                  URL: {tracker.url}
                </div>
                <div className="routes">
                  Redirect 1:
                </div>
                <div className="stats">
                  <div>
                    Unique:
                  </div>
                  <div>
                    Total:
                  </div>
                  <div>
                    Conversions:
                  </div>
                </div>
                <div className="routes">
                  Redirect 2:
                </div>
                <div className="stats">
                  <div>
                    Unique:
                  </div>
                  <div>
                    Total:
                  </div>
                  <div>
                    Conversions:
                  </div>
                </div>
              </div>
            )
          })
        }
        <div className="add-tracker">
          <div className="routes">
            URL: <input value={newUrl} onChange={(event)=>{setNewUrl(event.target.value)}}/>
          </div>
          <div className="routes">
            Redirect 1:
          </div>
          <div className="routes">
            Redirect 2:
          </div>
          <button onClick={handleNewTracker} style={{alignSelf: "center"}}>Add New Tracker</button>
        </div>
      </div>
    </div>
  );
}

export default App;
